                  an2=rsxy1(i1,i2,i3,axis1,1)
                  an3=rsxy1(i1,i2,i3,axis1,2)
                  aNorm=max(epsx,sqrt(an1**2+an2**2+an3**2))
                  an1=an1/aNorm
                  an2=an2/aNorm
                  an3=an3/aNorm
