     & 1),xy1(i1,i2,i3,2),t, ez, wex  )
               call ogderiv(ep, 0,0,1,0, xy1(i1,i2,i3,0),xy1(i1,i2,i3,
     & 1),xy1(i1,i2,i3,2),t, ez, wey  )
               call ogderiv(ep, 0,0,0,1, xy1(i1,i2,i3,0),xy1(i1,i2,i3,
     & 1),xy1(i1,i2,i3,2),t, ez, wez  )
               call ogderiv(ep, 0,2,0,0, xy1(i1,i2,i3,0),xy1(i1,i2,i3,
     & 1),xy1(i1,i2,i3,2),t, ez, wexx )
               call ogderiv(ep, 0,0,2,0, xy1(i1,i2,i3,0),xy1(i1,i2,i3,
     & 1),xy1(i1,i2,i3,2),t, ez, weyy )
               call ogderiv(ep, 0,0,0,2, xy1(i1,i2,i3,0),xy1(i1,i2,i3,
     & 1),xy1(i1,i2,i3,2),t, ez, wezz )
               ueLap = uexx+ueyy+uezz
               veLap = vexx+veyy+vezz
               weLap = wexx+weyy+wezz
               curlEex = wey-vez
               curlEey = uez-wex
               curlEez = vex-uey
               nDotCurlEe=an1*curlEex+an2*curlEey+an3*curlEez
               nDotLapEe=an1*ueLap+an2*veLap+an3*weLap
               f(0)= f(0) - ( (curlEex- nDotCurlEe*an1)*(1./mu1-1./mu2)
     &  )
               f(1)= f(1) - ( (curlEey- nDotCurlEe*an2)*(1./mu1-1./mu2)
     &  )
               f(2)= f(2) - ( (curlEez- nDotCurlEe*an3)*(1./mu1-1./mu2)
     &  )
               ! Assign extrap with TZ ??
               f(3)= f(3) - ( ueLap*(1./epsmu1-1./epsmu2) + nDotLapEe*
     & an1*(cem1-cem2) )
               f(4)= f(4) - ( veLap*(1./epsmu1-1./epsmu2) + nDotLapEe*
     & an2*(cem1-cem2) )
               f(5)= f(5) - ( weLap*(1./epsmu1-1./epsmu2) + nDotLapEe*
     & an3*(cem1-cem2) )
             end if

            if( debug.gt.4 )then
             write(debugFile,'(/," --> 3d-order4-curv: i1,i2,i3=",3i4,
     & " an1,an2,an3=",3e11.3)') i1,i2,i3,an1,an2,an3
             write(debugFile,'(" --> 3d-order4-curv: i1,i2,i3=",3i4," 
     & f(start)=",12e10.2)') i1,i2,i3,f(0),f(1),f(2),f(3),f(4),f(5),f(
     & 6),f(7),f(8),f(9),f(10),f(11)
             ! '
             write(debugFile,'(" --> u1x,u1y,u1z,v1x,v1y,v1z=",6f8.4)')
     &  u1x,u1y,u1z,v1x,v1y,v1z
             write(debugFile,'(" --> u2x,u2y,u2z,v2x,v2y,v2z=",6f8.4)')
     &  u2x,u2y,u2z,v2x,v2y,v2z

             write(debugFile,'(" --> vv1r,vv1s,vv1t         =",3e9.2)')
     &  vv1r,vv1s,vv1t
             do k3=-1,1
             do k2=-1,1
             write(debugFile,'(" --> v1: =",3f8.4)') u1(i1-1,i2+k2,i3+
     & k3,ey),u1(i1,i2+k2,i3+k3,ey),u1(i1+1,i2+k2,i3+k3,ey)
             end do
             end do
             do k3=-1,1
             do k2=-1,1
             write(debugFile,'(" --> v2: =",3f8.4)') u2(j1-1,j2+k2,j3+
     & k3,ey),u2(j1,j2+k2,j3+k3,ey),u2(j1+1,j2+k2,j3+k3,ey)
             end do
             end do
             ! '
            end if

            ! here is the matrix of coefficients for the unknowns u1(-1),v1(-1),w1(-1),  u2(-1),v2(-1),w2(-1)
            ! Solve:
            !     
            !       A [ U ] = A [ U(old) ] - [ f ]


            c1m1x = -is*8.*rsxy1(i1,i2,i3,axis1,0)/(12.*dr1(axis1))    ! coeff of u1(-1) from D.x
            c1m1y = -is*8.*rsxy1(i1,i2,i3,axis1,1)/(12.*dr1(axis1))    ! coeff of u1(-1) from D.y
            c1m1z = -is*8.*rsxy1(i1,i2,i3,axis1,2)/(12.*dr1(axis1))    ! coeff of u1(-1) from D.z

            c1m2x =  is   *rsxy1(i1,i2,i3,axis1,0)/(12.*dr1(axis1))    ! coeff of u1(-2) from D.x
            c1m2y =  is   *rsxy1(i1,i2,i3,axis1,1)/(12.*dr1(axis1))    ! coeff of u1(-2) from D.y
            c1m2z =  is   *rsxy1(i1,i2,i3,axis1,2)/(12.*dr1(axis1))    ! coeff of u1(-2) from D.z

            c2m1x = -js*8.*rsxy2(j1,j2,j3,axis2,0)/(12.*dr2(axis2))    ! coeff of u2(-1) from D.x
            c2m1y = -js*8.*rsxy2(j1,j2,j3,axis2,1)/(12.*dr2(axis2))
            c2m1z = -js*8.*rsxy2(j1,j2,j3,axis2,2)/(12.*dr2(axis2))

            c2m2x =  js   *rsxy2(j1,j2,j3,axis2,0)/(12.*dr2(axis2))
            c2m2y =  js   *rsxy2(j1,j2,j3,axis2,1)/(12.*dr2(axis2))
            c2m2z =  js   *rsxy2(j1,j2,j3,axis2,2)/(12.*dr2(axis2))

            rxx1(0,0,0)=aj1rxx
            rxx1(0,1,1)=aj1ryy
            rxx1(0,2,2)=aj1rzz
            rxx1(1,0,0)=aj1sxx
            rxx1(1,1,1)=aj1syy
            rxx1(1,2,2)=aj1szz
            rxx1(2,0,0)=aj1txx
            rxx1(2,1,1)=aj1tyy
            rxx1(2,2,2)=aj1tzz

            rxx2(0,0,0)=aj2rxx
            rxx2(0,1,1)=aj2ryy
            rxx2(0,2,2)=aj2rzz
            rxx2(1,0,0)=aj2sxx
            rxx2(1,1,1)=aj2syy
            rxx2(1,2,2)=aj2szz
            rxx2(2,0,0)=aj2txx
            rxx2(2,1,1)=aj2tyy
            rxx2(2,2,2)=aj2tzz

            ! clap1m1 : coeff of u(-1) from lap1 = u1.xx + u1.yy + u1.zz
            ! clap1m2 : coeff of u(-2) from lap1 = u1.xx + u1.yy + u1.zz

 ! here are the macros from deriv.maple (file=derivMacros.h)
 ! defineMacro lapCoeff4a(is,dr,ds) ( (-2/3.*rxx*is-2/3.*ryy*is)/dr+(4/3.*rx**2+4/3.*ry**2)/dr**2 )
 ! defineMacro lapCoeff4b(is,dr,ds) ( (1/12.*rxx*is+1/12.*ryy*is)/dr+(-1/12.*rx**2-1/12.*ry**2)/dr**2 )

            clap1m1=4./3.*(rsxy1(i1,i2,i3,axis1,0)**2+rsxy1(i1,i2,i3,
     & axis1,1)**2+rsxy1(i1,i2,i3,axis1,2)**2)/(dr1(axis1)**2) -is*
     & 2./3.*(rxx1(axis1,0,0)+rxx1(axis1,1,1)+rxx1(axis1,2,2))/(2.*
     & dr1(axis1))
            clap1m2=-1./12.*(rsxy1(i1,i2,i3,axis1,0)**2+rsxy1(i1,i2,i3,
     & axis1,1)**2+rsxy1(i1,i2,i3,axis1,2)**2)/(dr1(axis1)**2) +is*
     & 1./12.*(rxx1(axis1,0,0)+rxx1(axis1,1,1)+rxx1(axis1,2,2))/(2.*
     & dr1(axis1))

            clap2m1=4/3.*(rsxy2(j1,j2,j3,axis2,0)**2+rsxy2(j1,j2,j3,
     & axis2,1)**2+rsxy2(j1,j2,j3,axis2,2)**2)/(dr2(axis2)**2) -js*
     & 2./3.*(rxx2(axis2,0,0)+rxx2(axis2,1,1)+rxx2(axis2,2,2))/(2.*
     & dr2(axis2))
            clap2m2=-1./12.*(rsxy2(j1,j2,j3,axis2,0)**2+rsxy2(j1,j2,j3,
     & axis2,1)**2+rsxy2(j1,j2,j3,axis2,2)**2)/(dr2(axis2)**2) +js*
     & 1./12.*(rxx2(axis2,0,0)+rxx2(axis2,1,1)+rxx2(axis2,2,2))/(2.*
     & dr2(axis2))

            ! cdivE1 =  u.c1x + v.c1y + w.c1z
            ! nDotCurlE1 = (w1y-v1z)*an1 + (u1z-w1x)*an2 + (v1x-u1y)*an3

            ! 12 Unknowns:
            !   u1(-1), v1(-1), w1(-1), u1(-2), v1(-2), w1(-2), 
            !   u2(-1), v2(-1), w2(-1), u2(-2), v2(-2), w2(-2)  
            ! 12 Equations: 
            !    a12(eqn,unknown) 

            ! Equation 0: 
            ! (u.x+v.y+w.z)*an1 + ( w1y-v1z - nDotCurlE1*an1)/mu1
            a12(0,0) = ( c1m1x*an1 + (           - (c1m1z*an2-c1m1y*
     & an3)*an1 )/mu1 ) ! coeff of u1(-1)
            a12(0,1) = ( c1m1y*an1 + (    -c1m1z - (c1m1x*an3-c1m1z*
     & an1)*an1 )/mu1 ) ! coeff of v1(-1)
            a12(0,2) = ( c1m1z*an1 + ( c1m1y     - (c1m1y*an1-c1m1x*
     & an2)*an1 )/mu1 ) ! coeff of w1(-1)

            a12(0,3) = ( c1m2x*an1 + (           - (c1m2z*an2-c1m2y*
     & an3)*an1 )/mu1 ) ! coeff of u1(-1)
            a12(0,4) = ( c1m2y*an1 + (    -c1m2z - (c1m2x*an3-c1m2z*
     & an1)*an1 )/mu1 ) ! coeff of v1(-1)
            a12(0,5) = ( c1m2z*an1 + ( c1m2y     - (c1m2y*an1-c1m2x*
     & an2)*an1 )/mu1 ) ! coeff of w1(-1)

            a12(0,6) =-( c2m1x*an1 + (           - (c2m1z*an2-c2m1y*
     & an3)*an1 )/mu2 ) ! coeff of u2(-1)
            a12(0,7) =-( c2m1y*an1 + (    -c2m1z - (c2m1x*an3-c2m1z*
     & an1)*an1 )/mu2 ) ! coeff of v2(-1)
            a12(0,8) =-( c2m1z*an1 + ( c2m1y     - (c2m1y*an1-c2m1x*
     & an2)*an1 )/mu2 ) ! coeff of w2(-1)

            a12(0,9) =-( c2m2x*an1 + (           - (c2m2z*an2-c2m2y*
     & an3)*an1 )/mu2 ) ! coeff of u2(-1)
            a12(0,10)=-( c2m2y*an1 + (    -c2m2z - (c2m2x*an3-c2m2z*
     & an1)*an1 )/mu2 ) ! coeff of v2(-1)
            a12(0,11)=-( c2m2z*an1 + ( c2m2y     - (c2m2y*an1-c2m2x*
     & an2)*an1 )/mu2 ) ! coeff of w2(-1)

            ! Equation 1:
            ! (u.x+v.y+w.z)*an2 + ( u1z-w1x - nDotCurlE1*an2)/mu1
            a12(1,0) = ( c1m1x*an2 + ( c1m1z     - (c1m1z*an2-c1m1y*
     & an3)*an2 )/mu1 ) ! coeff of u1(-1)
            a12(1,1) = ( c1m1y*an2 + (           - (c1m1x*an3+c1m1z*
     & an1)*an2 )/mu1 ) ! coeff of v1(-1)
            a12(1,2) = ( c1m1z*an2 + (    -c1m1x - (c1m1y*an1-c1m1x*
     & an2)*an2 )/mu1 ) ! coeff of w1(-1)

            a12(1,3) = ( c1m2x*an2 + ( c1m2z     - (c1m2z*an2-c1m2y*
     & an3)*an2 )/mu1 ) ! coeff of u1(-1)
            a12(1,4) = ( c1m2y*an2 + (           - (c1m2x*an3+c1m2z*
     & an1)*an2 )/mu1 ) ! coeff of v1(-1)
            a12(1,5) = ( c1m2z*an2 + (    -c1m2x - (c1m2y*an1-c1m2x*
     & an2)*an2 )/mu1 ) ! coeff of w1(-1)

            a12(1,6) =-( c2m1x*an2 + ( c2m1z     - (c2m1z*an2-c2m1y*
     & an3)*an2 )/mu2 ) ! coeff of u2(-1)
            a12(1,7) =-( c2m1y*an2 + (           - (c2m1x*an3+c2m1z*
     & an1)*an2 )/mu2 ) ! coeff of v2(-1)
            a12(1,8) =-( c2m1z*an2 + (    -c2m1x - (c2m1y*an1-c2m1x*
     & an2)*an2 )/mu2 ) ! coeff of w2(-1)

            a12(1,9) =-( c2m2x*an2 + ( c2m2z     - (c2m2z*an2-c2m2y*
     & an3)*an2 )/mu2 ) ! coeff of u2(-1)
            a12(1,10)=-( c2m2y*an2 + (           - (c2m2x*an3+c2m2z*
     & an1)*an2 )/mu2 ) ! coeff of v2(-1)
            a12(1,11)=-( c2m2z*an2 + (    -c2m2x - (c2m2y*an1-c2m2x*
     & an2)*an2 )/mu2 ) ! coeff of w2(-1)

            ! Equation 2:
            ! (u.x+v.y+w.z)*an3 + ( v1x-u1y - nDotCurlE1*an2)/mu1
            a12(2,0) = ( c1m1x*an3 + (    -c1m1y - (c1m1z*an2-c1m1y*
     & an3)*an3 )/mu1 ) ! coeff of u1(-1)
            a12(2,1) = ( c1m1y*an3 + ( c1m1x     - (c1m1x*an3+c1m1z*
     & an1)*an3 )/mu1 ) ! coeff of v1(-1)
            a12(2,2) = ( c1m1z*an3 + (           - (c1m1y*an1-c1m1x*
     & an2)*an3 )/mu1 ) ! coeff of w1(-1)

            a12(2,3) = ( c1m2x*an3 + (    -c1m2y - (c1m2z*an2-c1m2y*
     & an3)*an3 )/mu1 ) ! coeff of u1(-1)
            a12(2,4) = ( c1m2y*an3 + ( c1m2x     - (c1m2x*an3+c1m2z*
     & an1)*an3 )/mu1 ) ! coeff of v1(-1)
            a12(2,5) = ( c1m2z*an3 + (           - (c1m2y*an1-c1m2x*
     & an2)*an3 )/mu1 ) ! coeff of w1(-1)

            a12(2,6) =-( c2m1x*an3 + (    -c2m1y - (c2m1z*an2-c2m1y*
     & an3)*an3 )/mu2 ) ! coeff of u2(-1)
            a12(2,7) =-( c2m1y*an3 + ( c2m1x     - (c2m1x*an3+c2m1z*
     & an1)*an3 )/mu2 ) ! coeff of v2(-1)
            a12(2,8) =-( c2m1z*an3 + (           - (c2m1y*an1-c2m1x*
     & an2)*an3 )/mu2 ) ! coeff of w2(-1)

            a12(2,9) =-( c2m2x*an3 + (    -c2m2y - (c2m2z*an2-c2m2y*
     & an3)*an3 )/mu2 ) ! coeff of u2(-1)
            a12(2,10)=-( c2m2y*an3 + ( c2m2x     - (c2m2x*an3+c2m2z*
     & an1)*an3 )/mu2 ) ! coeff of v2(-1)
            a12(2,11)=-( c2m2z*an3 + (           - (c2m2y*an1-c2m2x*
     & an2)*an3 )/mu2 ) ! coeff of w2(-1)

            ! Equation 3:
            !  u1Lap/(epsmu1) + cem1*( an1*u1Lap + an2*v1Lap + an3*w1Lap )*an1
            a12(3,0) = ( clap1m1/(epsmu1) + cem1*( an1*clap1m1         
     &                 )*an1 ) ! coeff of u1(-1)
            a12(3,1) = (                    cem1*(             an2*
     & clap1m1             )*an1 )
            a12(3,2) = (                    cem1*(                     
     &     an3*clap1m1 )*an1 )

            a12(3,3) = ( clap1m2/(epsmu1) + cem1*( an1*clap1m2         
     &                 )*an1 ) ! coeff of u1(-2)
            a12(3,4) = (                    cem1*(             an2*
     & clap1m2             )*an1 )
            a12(3,5) = (                    cem1*(                     
     &     an3*clap1m2 )*an1 )

            a12(3,6) =-( clap2m1/(epsmu2) + cem2*( an1*clap2m1         
     &                 )*an1 ) ! coeff of u2(-1)
            a12(3,7) =-(                    cem2*(             an2*
     & clap2m1             )*an1 )
            a12(3,8) =-(                    cem2*(                     
     &     an3*clap2m1 )*an1 )

            a12(3,9) =-( clap2m2/(epsmu2) + cem2*( an1*clap2m2         
     &                 )*an1 ) ! coeff of u2(-2)
            a12(3,10)=-(                    cem2*(             an2*
     & clap2m2             )*an1 )
            a12(3,11)=-(                    cem2*(                     
     &     an3*clap2m2 )*an1 )

            ! Equation 4:
            !  v1Lap/(epsmu1) + cem1*( an1*u1Lap + an2*v1Lap + an3*w1Lap )*an2
            a12(4,0) = (                    cem1*( an1*clap1m1         
     &                 )*an2 ) ! coeff of u1(-1)
            a12(4,1) = ( clap1m1/(epsmu1) + cem1*(             an2*
     & clap1m1             )*an2 )
            a12(4,2) = (                    cem1*(                     
     &     an3*clap1m1 )*an2 )

            a12(4,3) = (                    cem1*( an1*clap1m2         
     &                 )*an2 ) ! coeff of u1(-2)
            a12(4,4) = ( clap1m2/(epsmu1) + cem1*(             an2*
     & clap1m2             )*an2 )
            a12(4,5) = (                    cem1*(                     
     &     an3*clap1m2 )*an2 )

            a12(4,6) =-(                    cem2*( an1*clap2m1         
     &                 )*an2 ) ! coeff of u2(-1)
            a12(4,7) =-( clap2m1/(epsmu2) + cem2*(             an2*
     & clap2m1             )*an2 )
            a12(4,8) =-(                    cem2*(                     
     &     an3*clap2m1 )*an2 )

            a12(4,9) =-(                    cem2*( an1*clap2m2         
     &                 )*an2 ) ! coeff of u2(-2)
            a12(4,10)=-( clap2m2/(epsmu2) + cem2*(             an2*
     & clap2m2             )*an2 )
            a12(4,11)=-(                    cem2*(                     
     &     an3*clap2m2 )*an2 )

            ! Equation 5:
            !  w1Lap/(epsmu1) + cem1*( an1*u1Lap + an2*v1Lap + an3*w1Lap )*an3
            a12(5,0) = (                    cem1*( an1*clap1m1         
     &                 )*an3 ) ! coeff of u1(-1)
            a12(5,1) = (                    cem1*(             an2*
     & clap1m1             )*an3 )
            a12(5,2) = ( clap1m1/(epsmu1) + cem1*(                     
     &     an3*clap1m1 )*an3 )

            a12(5,3) = (                    cem1*( an1*clap1m2         
     &                 )*an3 ) ! coeff of u1(-2)
            a12(5,4) = (                    cem1*(             an2*
     & clap1m2             )*an3 )
            a12(5,5) = ( clap1m2/(epsmu1) + cem1*(                     
     &     an3*clap1m2 )*an3 )

            a12(5,6) =-(                    cem2*( an1*clap2m1         
     &                 )*an3 ) ! coeff of u2(-1)
            a12(5,7) =-(                    cem2*(             an2*
     & clap2m1             )*an3 )
            a12(5,8) =-( clap2m1/(epsmu2) + cem2*(                     
     &     an3*clap2m1 )*an3 )

            a12(5,9) =-(                    cem2*( an1*clap2m2         
     &                 )*an3 ) ! coeff of u2(-2)
            a12(5,10)=-(                    cem2*(             an2*
     & clap2m2             )*an3 )
            a12(5,11)=-( clap2m2/(epsmu2) + cem2*(                     
     &     an3*clap2m2 )*an3 )

            ! Equation 6..11 :  extrapolate 2nd ghost point 
            cex1=1.
            cex2=-5. ! ** fix me ** orderOfExtrapolation for 2nd ghost point
            do ii=6,11
              do jj=0,11
                a12(ii,jj)=0.
              end do
            end do
            a12(6,0)  = cex2   ! u1(-1)
            a12(6,3)  = cex1   ! u1(-2)

            a12(7,1)  = cex2   ! v1(-1)
            a12(7,4)  = cex1   ! v1(-2)

            a12(8,2)  = cex2   ! w1(-1)
            a12(8,5)  = cex1   ! w1(-2)

            a12(9,6)  = cex2   ! u2(-1)
            a12(9,9)  = cex1   ! u2(-2)

            a12(10,7) = cex2   ! v2(-1)
            a12(10,10)= cex1   ! v2(-2)

            a12(11,8) = cex2   ! w2(-1)
            a12(11,11)= cex1   ! w2(-2)

            ! fill in the current values for the unknowns: 
            q(0) = u1(i1-is1,i2-is2,i3-is3,ex)
            q(1) = u1(i1-is1,i2-is2,i3-is3,ey)
            q(2) = u1(i1-is1,i2-is2,i3-is3,ez)
            q(3) = u1(i1-2*is1,i2-2*is2,i3-2*is3,ex)
            q(4) = u1(i1-2*is1,i2-2*is2,i3-2*is3,ey)
