     & 2)*rx2(axis2,0)*rxx2(axis2,0,2)+2*rx2(axis2,2)*(2*rx2(axis2,0)*
     & rxx2(axis2,0,2)+rx2(axis2,2)*rxx2(axis2,0,0))+2*rxx2(axis2,2,2)
     & *rx2(axis2,0)**2+6*rx2(axis2,1)**2*rxx2(axis2,1,1)+6*rx2(axis2,
     & 0)**2*rxx2(axis2,0,0)+4*rx2(axis2,2)*rx2(axis2,1)*rxx2(axis2,1,
     & 2)+2*rx2(axis2,2)*(2*rx2(axis2,1)*rxx2(axis2,1,2)+rx2(axis2,2)*
     & rxx2(axis2,1,1))+2*rxx2(axis2,2,2)*rx2(axis2,1)**2+6*rx2(axis2,
     & 2)**2*rxx2(axis2,2,2))*js/dr2(axis2)**3+(2*rx2(axis2,2)**2*rx2(
     & axis2,1)**2+rx2(axis2,2)**4+rx2(axis2,1)**4+2*rx2(axis2,2)**2*
     & rx2(axis2,0)**2+2*rx2(axis2,1)**2*rx2(axis2,0)**2+rx2(axis2,0)*
     & *4)/dr2(axis2)**4)/epsmu2

            ! write(debugFile,'(" --> 3d-order4-c: i1,i2,i3=",3i4," c1m1x,c1m1y,c1m1z,clap1m1,clap1m2=",12e10.2)') i1,i2,i3,c1m1x,c1m1y,c1m1z,clap1m1,clap1m2

            ! fill equations 6...11
             ! Equation 0: 
             ! (u.x+v.y+w.z)*an1 + ( w1y-v1z - nDotCurlE1*an1)/mu1
             a12(6,0) = ( c1m1x*an1 + (           - (c1m1z*an2-c1m1y*
     & an3)*an1 )/mu1 ) ! coeff of u1(-1)
             a12(6,1) = ( c1m1y*an1 + (    -c1m1z - (c1m1x*an3-c1m1z*
