             a12(6,3) = ( c1m2x*an1 + (           - (c1m2z*an2-c1m2y*
     & an3)*an1 )/mu1 ) ! coeff of u1(-1)
             a12(6,4) = ( c1m2y*an1 + (    -c1m2z - (c1m2x*an3-c1m2z*
     & an1)*an1 )/mu1 ) ! coeff of v1(-1)
             a12(6,5) = ( c1m2z*an1 + ( c1m2y     - (c1m2y*an1-c1m2x*
     & an2)*an1 )/mu1 ) ! coeff of w1(-1)
             a12(6,6) =-( c2m1x*an1 + (           - (c2m1z*an2-c2m1y*
     & an3)*an1 )/mu2 ) ! coeff of u2(-1)
             a12(6,7) =-( c2m1y*an1 + (    -c2m1z - (c2m1x*an3-c2m1z*
     & an1)*an1 )/mu2 ) ! coeff of v2(-1)
             a12(6,8) =-( c2m1z*an1 + ( c2m1y     - (c2m1y*an1-c2m1x*
     & an2)*an1 )/mu2 ) ! coeff of w2(-1)
             a12(6,9) =-( c2m2x*an1 + (           - (c2m2z*an2-c2m2y*
     & an3)*an1 )/mu2 ) ! coeff of u2(-1)
             a12(6,10)=-( c2m2y*an1 + (    -c2m2z - (c2m2x*an3-c2m2z*
     & an1)*an1 )/mu2 ) ! coeff of v2(-1)
             a12(6,11)=-( c2m2z*an1 + ( c2m2y     - (c2m2y*an1-c2m2x*
     & an2)*an1 )/mu2 ) ! coeff of w2(-1)
             ! Equation 1:
             ! (u.x+v.y+w.z)*an2 + ( u1z-w1x - nDotCurlE1*an2)/mu1
             a12(7,0) = ( c1m1x*an2 + ( c1m1z     - (c1m1z*an2-c1m1y*
     & an3)*an2 )/mu1 ) ! coeff of u1(-1)
             a12(7,1) = ( c1m1y*an2 + (           - (c1m1x*an3+c1m1z*
     & an1)*an2 )/mu1 ) ! coeff of v1(-1)
             a12(7,2) = ( c1m1z*an2 + (    -c1m1x - (c1m1y*an1-c1m1x*
     & an2)*an2 )/mu1 ) ! coeff of w1(-1)
             a12(7,3) = ( c1m2x*an2 + ( c1m2z     - (c1m2z*an2-c1m2y*
     & an3)*an2 )/mu1 ) ! coeff of u1(-1)
             a12(7,4) = ( c1m2y*an2 + (           - (c1m2x*an3+c1m2z*
     & an1)*an2 )/mu1 ) ! coeff of v1(-1)
             a12(7,5) = ( c1m2z*an2 + (    -c1m2x - (c1m2y*an1-c1m2x*
     & an2)*an2 )/mu1 ) ! coeff of w1(-1)
             a12(7,6) =-( c2m1x*an2 + ( c2m1z     - (c2m1z*an2-c2m1y*
     & an3)*an2 )/mu2 ) ! coeff of u2(-1)
             a12(7,7) =-( c2m1y*an2 + (           - (c2m1x*an3+c2m1z*
     & an1)*an2 )/mu2 ) ! coeff of v2(-1)
             a12(7,8) =-( c2m1z*an2 + (    -c2m1x - (c2m1y*an1-c2m1x*
     & an2)*an2 )/mu2 ) ! coeff of w2(-1)
             a12(7,9) =-( c2m2x*an2 + ( c2m2z     - (c2m2z*an2-c2m2y*
     & an3)*an2 )/mu2 ) ! coeff of u2(-1)
             a12(7,10)=-( c2m2y*an2 + (           - (c2m2x*an3+c2m2z*
     & an1)*an2 )/mu2 ) ! coeff of v2(-1)
             a12(7,11)=-( c2m2z*an2 + (    -c2m2x - (c2m2y*an1-c2m2x*
     & an2)*an2 )/mu2 ) ! coeff of w2(-1)
             ! Equation 2:
             ! (u.x+v.y+w.z)*an3 + ( v1x-u1y - nDotCurlE1*an2)/mu1
             a12(8,0) = ( c1m1x*an3 + (    -c1m1y - (c1m1z*an2-c1m1y*
     & an3)*an3 )/mu1 ) ! coeff of u1(-1)
             a12(8,1) = ( c1m1y*an3 + ( c1m1x     - (c1m1x*an3+c1m1z*
     & an1)*an3 )/mu1 ) ! coeff of v1(-1)
             a12(8,2) = ( c1m1z*an3 + (           - (c1m1y*an1-c1m1x*
     & an2)*an3 )/mu1 ) ! coeff of w1(-1)
             a12(8,3) = ( c1m2x*an3 + (    -c1m2y - (c1m2z*an2-c1m2y*
     & an3)*an3 )/mu1 ) ! coeff of u1(-1)
             a12(8,4) = ( c1m2y*an3 + ( c1m2x     - (c1m2x*an3+c1m2z*
     & an1)*an3 )/mu1 ) ! coeff of v1(-1)
             a12(8,5) = ( c1m2z*an3 + (           - (c1m2y*an1-c1m2x*
     & an2)*an3 )/mu1 ) ! coeff of w1(-1)
             a12(8,6) =-( c2m1x*an3 + (    -c2m1y - (c2m1z*an2-c2m1y*
     & an3)*an3 )/mu2 ) ! coeff of u2(-1)
             a12(8,7) =-( c2m1y*an3 + ( c2m1x     - (c2m1x*an3+c2m1z*
     & an1)*an3 )/mu2 ) ! coeff of v2(-1)
             a12(8,8) =-( c2m1z*an3 + (           - (c2m1y*an1-c2m1x*
     & an2)*an3 )/mu2 ) ! coeff of w2(-1)
             a12(8,9) =-( c2m2x*an3 + (    -c2m2y - (c2m2z*an2-c2m2y*
     & an3)*an3 )/mu2 ) ! coeff of u2(-1)
             a12(8,10)=-( c2m2y*an3 + ( c2m2x     - (c2m2x*an3+c2m2z*
     & an1)*an3 )/mu2 ) ! coeff of v2(-1)
             a12(8,11)=-( c2m2z*an3 + (           - (c2m2y*an1-c2m2x*
     & an2)*an3 )/mu2 ) ! coeff of w2(-1)
             ! Equation 3:
             !  u1Lap/(epsmu1) + cem1*( an1*u1Lap + an2*v1Lap + an3*w1Lap )*an1
             a12(9,0) = ( clap1m1/(epsmu1) + cem1*( an1*clap1m1        
     &                  )*an1 ) ! coeff of u1(-1)
             a12(9,1) = (                    cem1*(             an2*
     & clap1m1             )*an1 )
             a12(9,2) = (                    cem1*(                    
     &      an3*clap1m1 )*an1 )
             a12(9,3) = ( clap1m2/(epsmu1) + cem1*( an1*clap1m2        
     &                  )*an1 ) ! coeff of u1(-2)
             a12(9,4) = (                    cem1*(             an2*
     & clap1m2             )*an1 )
             a12(9,5) = (                    cem1*(                    
     &      an3*clap1m2 )*an1 )
             a12(9,6) =-( clap2m1/(epsmu2) + cem2*( an1*clap2m1        
     &                  )*an1 ) ! coeff of u2(-1)
             a12(9,7) =-(                    cem2*(             an2*
     & clap2m1             )*an1 )
             a12(9,8) =-(                    cem2*(                    
     &      an3*clap2m1 )*an1 )
             a12(9,9) =-( clap2m2/(epsmu2) + cem2*( an1*clap2m2        
     &                  )*an1 ) ! coeff of u2(-2)
             a12(9,10)=-(                    cem2*(             an2*
     & clap2m2             )*an1 )
             a12(9,11)=-(                    cem2*(                    
     &      an3*clap2m2 )*an1 )
             ! Equation 4:
             !  v1Lap/(epsmu1) + cem1*( an1*u1Lap + an2*v1Lap + an3*w1Lap )*an2
             a12(10,0) = (                    cem1*( an1*clap1m1       
     &                   )*an2 ) ! coeff of u1(-1)
             a12(10,1) = ( clap1m1/(epsmu1) + cem1*(             an2*
     & clap1m1             )*an2 )
             a12(10,2) = (                    cem1*(                   
     &       an3*clap1m1 )*an2 )
             a12(10,3) = (                    cem1*( an1*clap1m2       
