             a12(11,5) = ( clap1m2/(epsmu1) + cem1*(                   
     &       an3*clap1m2 )*an3 )
             a12(11,6) =-(                    cem2*( an1*clap2m1       
     &                   )*an3 ) ! coeff of u2(-1)
             a12(11,7) =-(                    cem2*(             an2*
     & clap2m1             )*an3 )
             a12(11,8) =-( clap2m1/(epsmu2) + cem2*(                   
     &       an3*clap2m1 )*an3 )
             a12(11,9) =-(                    cem2*( an1*clap2m2       
     &                   )*an3 ) ! coeff of u2(-2)
             a12(11,10)=-(                    cem2*(             an2*
     & clap2m2             )*an3 )
             a12(11,11)=-( clap2m2/(epsmu2) + cem2*(                   
     &       an3*clap2m2 )*an3 )

            ! Equation 6..11 :  extrapolate 2nd ghost point 
