            q(9) = u2(j1-2*js1,j2-2*js2,j3-2*js3,ex)
            q(10)= u2(j1-2*js1,j2-2*js2,j3-2*js3,ey)
            q(11)= u2(j1-2*js1,j2-2*js2,j3-2*js3,ez)

            ! subtract off the contributions from the wrong values at the ghost points:
            numberOfEquations=12
            do n=0,numberOfEquations-1
              f(n) = (a12(n,0)*q(0)+a12(n,1)*q(1)+a12(n,2)*q(2)+a12(n,
     & 3)*q(3)+a12(n,4)*q(4)+a12(n,5)*q(5)+a12(n,6)*q(6)+a12(n,7)*q(7)
     & +a12(n,8)*q(8)+a12(n,9)*q(9)+a12(n,10)*q(10)+a12(n,11)*q(11) ) 
     & - f(n)
            end do
            if( debug.gt.3 )then
              write(debugFile,'(" --> 3d:order4-c: f(subtract)=",
     & 12e10.2)') (f(n),n=0,11)
            end if
            ! solve A Q = F
            ! factor the matrix
            call dgeco( a12(0,0), numberOfEquations, numberOfEquations,
     &  ipvt(0),rcond,work(0))
            ! solve
             if( debug.gt.3 )then
               write(debugFile,'(" --> 3d:order4-c: rcond=",e10.2)') 
     & rcond
             end if
            job=0
            call dgesl( a12(0,0), numberOfEquations, numberOfEquations,
     &  ipvt(0), f(0), job)
            if( debug.gt.3 )then
              write(debugFile,'(" --> 3d:order4-c: f(solve)=",12e10.2)
     & ') (f(n),n=0,11)
              write(debugFile,'(" --> 3d:order4-c:        q=",12e10.2)
     & ') (q(n),n=0,11)
              write(debugFile,'(" --> 3d:order4-c:      f-q=",12e10.2)
     & ') (f(n)-q(n),n=0,11)
            end if
            ! fill in the answer:
            u1(i1-is1,i2-is2,i3-is3,ex)      =f(0 )*omega+(1.-omega)*q(
     & 0)
            u1(i1-is1,i2-is2,i3-is3,ey)      =f(1 )*omega+(1.-omega)*q(
     & 1)
            u1(i1-is1,i2-is2,i3-is3,ez)      =f(2 )*omega+(1.-omega)*q(
     & 2)
            u1(i1-2*is1,i2-2*is2,i3-2*is3,ex)=f(3 )*omega+(1.-omega)*q(
     & 3)
            u1(i1-2*is1,i2-2*is2,i3-2*is3,ey)=f(4 )*omega+(1.-omega)*q(
     & 4)
            u1(i1-2*is1,i2-2*is2,i3-2*is3,ez)=f(5 )*omega+(1.-omega)*q(
     & 5)

            u2(j1-js1,j2-js2,j3-js3,ex)      =f(6 )*omega+(1.-omega)*q(
     & 6)
            u2(j1-js1,j2-js2,j3-js3,ey)      =f(7 )*omega+(1.-omega)*q(
     & 7)
            u2(j1-js1,j2-js2,j3-js3,ez)      =f(8 )*omega+(1.-omega)*q(
     & 8)
            u2(j1-2*js1,j2-2*js2,j3-2*js3,ex)=f(9 )*omega+(1.-omega)*q(
     & 9)
            u2(j1-2*js1,j2-2*js2,j3-2*js3,ey)=f(10)*omega+(1.-omega)*q(
     & 10)
            u2(j1-2*js1,j2-2*js2,j3-2*js3,ez)=f(11)*omega+(1.-omega)*q(
     & 11)


            ! compute the maximum change in the solution for this iteration
            do n=0,11
              err=max(err,abs(q(n)-f(n)))
            end do

            if( debug.gt.3 )then ! re-evaluate

             ! turn this off for now:
              ! These derivatives are computed to 2nd-order accuracy
              ! NOTE: the jacobian derivatives can be computed once for all components
               ! this next call will define the jacobian and its derivatives (parameteric and spatial)
               aj1rx = rsxy1(i1,i2,i3,0,0)
               aj1rxr = (-rsxy1(i1-1,i2,i3,0,0)+rsxy1(i1+1,i2,i3,0,0))
     & /(2.*dr1(0))
               aj1rxs = (-rsxy1(i1,i2-1,i3,0,0)+rsxy1(i1,i2+1,i3,0,0))
     & /(2.*dr1(1))
               aj1rxt = (-rsxy1(i1,i2,i3-1,0,0)+rsxy1(i1,i2,i3+1,0,0))
     & /(2.*dr1(2))
               aj1rxrr = (rsxy1(i1-1,i2,i3,0,0)-2.*rsxy1(i1,i2,i3,0,0)+
     & rsxy1(i1+1,i2,i3,0,0))/(dr1(0)**2)
               aj1rxrs = (-(-rsxy1(i1-1,i2-1,i3,0,0)+rsxy1(i1-1,i2+1,
     & i3,0,0))/(2.*dr1(1))+(-rsxy1(i1+1,i2-1,i3,0,0)+rsxy1(i1+1,i2+1,
     & i3,0,0))/(2.*dr1(1)))/(2.*dr1(0))
               aj1rxss = (rsxy1(i1,i2-1,i3,0,0)-2.*rsxy1(i1,i2,i3,0,0)+
     & rsxy1(i1,i2+1,i3,0,0))/(dr1(1)**2)
               aj1rxrt = (-(-rsxy1(i1-1,i2,i3-1,0,0)+rsxy1(i1-1,i2,i3+
     & 1,0,0))/(2.*dr1(2))+(-rsxy1(i1+1,i2,i3-1,0,0)+rsxy1(i1+1,i2,i3+
     & 1,0,0))/(2.*dr1(2)))/(2.*dr1(0))
               aj1rxst = (-(-rsxy1(i1,i2-1,i3-1,0,0)+rsxy1(i1,i2-1,i3+
     & 1,0,0))/(2.*dr1(2))+(-rsxy1(i1,i2+1,i3-1,0,0)+rsxy1(i1,i2+1,i3+
     & 1,0,0))/(2.*dr1(2)))/(2.*dr1(1))
