                call ogderiv(ep, 0,2,2,0, x1,y1,z1,t, ez, wexxyy )
                call ogderiv(ep, 0,2,0,2, x1,y1,z1,t, ez, wexxzz )
                call ogderiv(ep, 0,0,4,0, x1,y1,z1,t, ez, weyyyy )
                call ogderiv(ep, 0,0,2,2, x1,y1,z1,t, ez, weyyzz )
                call ogderiv(ep, 0,0,0,4, x1,y1,z1,t, ez, wezzzz )
                ueLap = uexx+ueyy+uezz
                veLap = vexx+veyy+vezz
                weLap = wexx+weyy+wezz
                curlEex = wey-vez
                curlEey = uez-wex
                curlEez = vex-uey
                nDotCurlEe=an1*curlEex+an2*curlEey+an3*curlEez
                nDotLapEe=an1*ueLap+an2*veLap+an3*weLap
                f(0)= f(0) - ( (curlEex- nDotCurlEe*an1)*(1./mu1-
     & 1./mu2) )
                f(1)= f(1) - ( (curlEey- nDotCurlEe*an2)*(1./mu1-
     & 1./mu2) )
                f(2)= f(2) - ( (curlEez- nDotCurlEe*an3)*(1./mu1-
     & 1./mu2) )
                f(3)= f(3) - ( ueLap*(1./epsmu1-1./epsmu2) + nDotLapEe*
     & an1*(cem1-cem2) )
                f(4)= f(4) - ( veLap*(1./epsmu1-1./epsmu2) + nDotLapEe*
     & an2*(cem1-cem2) )
                f(5)= f(5) - ( weLap*(1./epsmu1-1./epsmu2) + nDotLapEe*
     & an3*(cem1-cem2) )
                ueLapSq = uexxxx+ueyyyy+uezzzz +2.*( uexxyy+uexxzz+
     & ueyyzz )
                veLapSq = vexxxx+veyyyy+vezzzz +2.*( vexxyy+vexxzz+
     & veyyzz )
                weLapSq = wexxxx+weyyyy+wezzzz +2.*( wexxyy+wexxzz+
     & weyyzz )
                curlLapEex = wexxy-vexxz + weyyy-veyyz + weyzz-vezzz
                curlLapEey = uexxz-wexxx + ueyyz-wexyy + uezzz-wexzz
                curlLapEez = vexxx-uexxy + vexyy-ueyyy + vexzz-ueyzz
                nDotCurlLapEe=an1*curlLapEex+an2*curlLapEey+an3*
     & curlLapEez
                nDotLapSqEe=an1*ueLapSq+an2*veLapSq+an3*weLapSq
              ! *e678*
                f(6) = f(6) - ( (curlLapEex- nDotCurlLapEe*an1)*(1./(
